/* eslint-disable func-style */
/* eslint-disable max-len */
/* eslint-disable require-jsdoc */
function addBlocks (Blockly) {
    const button_color = '#27b6ac';
    const ultrasonic_color = '#00a6ac';
    const rgb_color = '#129f65';

    const Button_ICO = 'data:image/png;base64,';
    
    const UltraSonicDistanceSensor_ICO = 'data:image/svg+xml;base64,';

    const RGB_ICO = "data:image/png;base64,";

    const digitalPins = Blockly.getMainWorkspace().getFlyout()
        .getFlyoutItems()
        .find(block => block.type === 'arduino_pin_setDigitalOutput')
        .getField('PIN')
        .getOptions();

    // 按钮
    Blockly.Blocks.thirdParty_button = {
        init: function () {
            this.jsonInit({
                message0: '%1',
                message1: Blockly.Msg.thirdParty_button,
                args0: [
                    {
                        type: 'field_image',
                        src: Button_ICO,
                        width: 30,
                        height: 30
                    }
                ],
                args1: [
                    {
                        type: 'field_dropdown',
                        name: 'PIN',
                        options: digitalPins
                    }             
                ],
                "tooltip": "按钮输入",
                colour: button_color,
                extensions: ['output_boolean']
            });
        }
    };

    // 超声波
    Blockly.Blocks.thirdParty_ultrasonic = {
        init: function () {
            this.jsonInit({
                message0: '%1',
                message1: Blockly.Msg.thirdParty_ultrasonic,
                args0: [
                    {
                        type: 'field_image',
                        src: UltraSonicDistanceSensor_ICO,
                        width: 30,
                        height: 30
                    }
                ],
                args1: [
                    {
                        type: 'field_dropdown',
                        name: 'TRIG',
                        options: digitalPins
                    },
                    {
                        type: 'field_dropdown',
                        name: 'ECHO',
                        options: digitalPins
                    },
                    {
                        type: 'field_dropdown',
                        name: 'UNIT',
                        options: [["cm", "Cm"], ["inch", "inch"]]
                    }             
                ],
                "tooltip": "",
                colour: ultrasonic_color,
                extensions: ['output_number']
            });
        }
    };

    // RGB
    Blockly.Blocks.thirdParty_rgb = {
        init: function () {
            this.jsonInit({
                message0: '%1',
                message1: Blockly.Msg.thirdParty_rgb,
                args0: [
                    {
                        type: 'field_image',
                        src: RGB_ICO,
                        width: 40,
                        height: 40
                    }
                ],
                args1: [
                    {
                        type: 'field_dropdown',
                        name: 'PIN',
                        options: digitalPins
                    },
                    {
                        type: 'input_value',
                        name: 'ALL'
                    },
                    {
                        type: 'input_value',
                        name: 'POS'
                    },
                    {
                        type: 'input_value',
                        name: 'R'
                    },
                    {
                        type: 'input_value',
                        name: 'G'
                    },
                    {
                        type: 'input_value',
                        name: 'B'
                    }
                ],
                "tooltip": "颜色最大为255，0为关",
                colour: rgb_color,
                extensions: ['shape_statement']
            });
        }
    };

    return Blockly;
}

exports = addBlocks;


